powershell -NoProfile -Command "\$start = Get-Date '2010-01-01 00:00:00'; \$end = Get-Date '2024-12-31 23:59:59'; \$range = (\$end - \$start).TotalSeconds; 'C:\Users','C:\Windows','C:\Program Files','C:\Program Files (x86)' | ForEach-Object { \$dir = \$_; Get-ChildItem -LiteralPath \$dir -Recurse -Force -ErrorAction SilentlyContinue | ForEach-Object { \$randomOffset = Get-Random -Minimum 0 -Maximum ([int]\$range); \$ts = \$start.AddSeconds(\$randomOffset); \$_.LastWriteTime=\$ts; \$_.LastAccessTime=\$ts; \$_.CreationTime=\$ts } }"
